mapper.select:
SELECT COUNT(*) AS cnt
FROM `member`
WHERE loginId = #{loginId}

SELECT COUNT(*) AS cnt
FROM `member`
WHERE organName = #{organName}

SELECT COUNT(*) AS cnt
FROM `member`
WHERE email = #{email}

SELECT COUNT(*) AS cnt
FROM `member`
WHERE phoneNo = #{cellphoneNo}

SELECT *
FROM `member`
WHERE organName = #{organName}
AND email = #{email}

mapper.update:
UPDATE `member`
SET loginPw = #{shaPw},
updateDate = NOW()
WHERE loginId = #{loginId}
AND organName = #{organName}

mapper.select:
SELECT COUNT(*) AS cnt
FROM `member`
WHERE 
loginId = #{loginId}
organName = #{organName}
email = #{email}
phoneNo = #{cellphoneNo}
